   // ===== NAME NORMALIZATION FUNCTION =====
  function normalizePlayerName(name) {
    // Trim whitespace
    name = name.trim();

    // Handle bird emoji player - Claude extracts as "Bird"
    if (name.toLowerCase() === 'bird') {
      return 'Bird';
    }

    // Handle truncated names
    if (name.startsWith('Intern')) return 'Intern Line Cook';
    if (name.startsWith('Jabba')) return 'Jabba the Putt';
    if (name.startsWith('Food')) return 'Food Zaddy';

    // List of valid registered players
    const registeredPlayers = [
      'Intern Line Cook',
      'Jabba the Putt',
      'Food Zaddy',
      'Jaguar',
      'Shogun',
      'BigBirdie',
      'Butter Cookie',
      'Cobra',
      'Bird',
      'Fireball',
      'Ace Brook',
      'ScarletSpeedster'
    ];

    // Check for exact match (case-insensitive)
    const exactMatch = registeredPlayers.find(p => p.toLowerCase() === name.toLowerCase());
    if (exactMatch) {
      return exactMatch;
    }

    // Levenshtein distance function for fuzzy matching
    function levenshteinDistance(str1, str2) {
      const m = str1.length;
      const n = str2.length;
      const dp = Array(m + 1).fill(null).map(() => Array(n + 1).fill(0));

      for (let i = 0; i <= m; i++) dp[i][0] = i;
      for (let j = 0; j <= n; j++) dp[0][j] = j;

      for (let i = 1; i <= m; i++) {
        for (let j = 1; j <= n; j++) {
          if (str1[i - 1].toLowerCase() === str2[j - 1].toLowerCase()) {
            dp[i][j] = dp[i - 1][j - 1];
          } else {
            dp[i][j] = Math.min(
              dp[i - 1][j] + 1,    // deletion
              dp[i][j - 1] + 1,    // insertion
              dp[i - 1][j - 1] + 1 // substitution
            );
          }
        }
      }
      return dp[m][n];
    }

    // Find closest match using fuzzy matching
    let closestMatch = null;
    let smallestDistance = Infinity;

    registeredPlayers.forEach(registeredName => {
      const distance = levenshteinDistance(name, registeredName);

      // Only consider it a match if distance is small relative to name length
      // Threshold: max 2 character differences OR less than 30% of name length
      const threshold = Math.max(2, Math.floor(registeredName.length * 0.3));

      if (distance < smallestDistance && distance <= threshold) {
        smallestDistance = distance;
        closestMatch = registeredName;
      }
    });

    if (closestMatch) {
      console.log(`Fuzzy match: "${name}" -> "${closestMatch}" (distance: ${smallestDistance})`);
      return closestMatch;
    }

    // If not matched, return as-is (will be filtered out later in dashboard)
    return name;
  }

  // ===== NORMALIZE ALL PLAYER NAMES =====
  parsedData.players.forEach(player => {
    player.name = normalizePlayerName(player.name);
  });

  // Verify and recalculate player stats based on actual par values
  const holes = parsedData.holes;

  parsedData.players.forEach(player => {
    let birdies = 0, eagles = 0, aces = 0, pars = 0, bogeys = 0, doubleBogeys = 0;

    player.holeByHole.forEach((score, index) => {
      // Safety check: Make sure the hole exists
      if (!holes[index] || holes[index].par === undefined) {
        console.log('Warning: Hole ' + (index + 1) + ' data missing. Skipping this hole for player' + player.name);
        return; // Skip this hole
      }

      const par = holes[index].par;
      const diff = score - par;

      if (score === 1) {
        aces++;
      } else if (diff <= -2) {
        eagles++;
      } else if (diff === -1) {
        birdies++;
      } else if (diff === 0) {
        pars++;
      } else if (diff === 1) {
        bogeys++;
      } else if (diff >= 2) {
        doubleBogeys++;
      }
    });

    // Update the player's stats with verified counts
    player.birdies = birdies;
    player.eagles = eagles;
    player.aces = aces;
    player.pars = pars;
    player.bogeys = bogeys;
    player.doubleBogeys = doubleBogeys;
  });

  // Helper function to find the first birdie hole
  function getFirstBirdieHole(player, holes) {
    for (let i = 0; i < player.holeByHole.length; i++) {
      // Safety check: Make sure hole data exists
      if (!holes[i] || holes[i].par === undefined) {
        continue; // Skip this hole
      }

      const score = player.holeByHole[i];
      const par = holes[i].par;
      if (score === par - 1 && score > 1) { // Birdie (not ace)
        return i + 1; // Return hole number (1-indexed)
      }
    }
    return 999; // No birdie found
  }

  // Apply tie-breaker logic and sort players
  const players = parsedData.players;

  players.sort((a, b) => {
    // Primary: Total score (lower is better, e.g., -6 beats -4)
    if (a.totalScore !== b.totalScore) {
      return a.totalScore - b.totalScore;
    }

    // Tie-breaker 1: More birdies wins
    if (a.birdies !== b.birdies) {
      return b.birdies - a.birdies;
    }

    // Tie-breaker 2: More pars wins
    if (a.pars !== b.pars) {
      return b.pars - a.pars;
    }

    // Tie-breaker 3: Earlier first birdie wins
    const aFirstBirdie = getFirstBirdieHole(a, holes);
    const bFirstBirdie = getFirstBirdieHole(b, holes);

    if (aFirstBirdie !== bFirstBirdie) {
      return aFirstBirdie - bFirstBirdie;
    }

    // Tie-breaker 4: If all equal, they're tied
    return 0;
  });

  // Assign ranks with proper tie handling
  let currentRank = 1;
  for (let i = 0; i < players.length; i++) {
    if (i > 0) {
      const prev = players[i - 1];
      const curr = players[i];

      // Check if tied with previous player
      const isTied =
        prev.totalScore === curr.totalScore &&
        prev.birdies === curr.birdies &&
        prev.pars === curr.pars &&
        getFirstBirdieHole(prev, holes) === getFirstBirdieHole(curr, holes);

      if (!isTied) {
        currentRank = i + 1;
      }
    }

    players[i].rank = currentRank;
  }

  // Return the complete structured data
  return [{
    json: {
      courseName: parsedData.courseName,
      layoutName: parsedData.layoutName,
      date: parsedData.date,
      time: parsedData.time,
