// Get the ranked player data from previous node
const roundData = $input.all()[0].json;

// ===== COURSE DIFFICULTY TIERS =====
// You can modify these tiers and courses as needed
const courseTiers = {
  "tier1": {
    multiplier: 1.0,
    courses: ["Wells Branch", "Lil G", "Armadillo Mini"]
  },
  "tier2": {
    multiplier: 1.5,
    courses: ["Zilker Park", "Live Oak", "Bartholomew Park", "Live Oak Brewing DGC"]
  },
  "tier3": {
    multiplier: 2.0,
    courses: ["Northtown Park", "Searight Park", "MetCenter", "Old Settler's", "Cat Hollow", "Circle C", "Williamson County"]
  },
  "tier4": {
    multiplier: 2.5,
    courses: ["East Metro", "Sprinkle Valley", "Roy G Guerrero", "Bible Ridge", "Flying Armadillo", "Harvey Penick"]
  }
};

// ===== RANK POINTS SYSTEM =====
const rankPoints = {
  1: 10,
  2: 7,
  3: 5
};
const defaultRankPoints = 2; // Everyone else who played

// ===== PERFORMANCE BONUSES =====
const performancePoints = {
  birdie: 1,
  eagle: 3,
  ace: 5
};

// ===== FIND COURSE MULTIPLIER =====
function getCourseMultiplier(courseName) {
  for (const tier in courseTiers) {
    const tierData = courseTiers[tier];
    // Check if course name matches (case-insensitive partial match)
    const matchedCourse = tierData.courses.find(course => 
      courseName.toLowerCase().includes(course.toLowerCase()) ||
      course.toLowerCase().includes(courseName.toLowerCase())
    );
    if (matchedCourse) {
      return tierData.multiplier;
    }
  }
  // Default to Tier 1 if course not found
  console.log(`Warning: Course "${courseName}" not found in tiers. Using 1x multiplier.`);
  return 1.0;
}

// ===== CALCULATE TIE-ADJUSTED RANK POINTS =====
function calculateRankPoints(players) {
  const rankPointsMap = {};

  let i = 0;
  while (i < players.length) {
    const currentRank = players[i].rank;

    // Find all players with this rank (ties)
    let tiedPlayers = 1;
    let j = i + 1;
    while (j < players.length && players[j].rank === currentRank) {
      tiedPlayers++;
      j++;
    }

    // Calculate average points for tied players
    let totalPoints = 0;
    for (let k = 0; k < tiedPlayers; k++) {
      const rank = currentRank + k;
      totalPoints += rankPoints[rank] || defaultRankPoints;
    }
    const avgPoints = totalPoints / tiedPlayers;

    // Assign average points to all tied players
    for (let k = i; k < j; k++) {
      rankPointsMap[k] = avgPoints;
    }

    i = j;
  }

  return rankPointsMap;
}

// ===== CALCULATE POINTS FOR EACH PLAYER =====
const courseMultiplier = getCourseMultiplier(roundData.courseName);
const rankPointsMap = calculateRankPoints(roundData.players);

roundData.players.forEach((player, index) => {
  // Base rank points (with tie averaging)
  const baseRankPoints = rankPointsMap[index] || defaultRankPoints;

  // Performance bonuses
  const birdiePoints = player.birdies * performancePoints.birdie;
  const eaglePoints = player.eagles * performancePoints.eagle;
  const acePoints = player.aces * performancePoints.ace;

  // Total before multiplier
  const rawPoints = baseRankPoints + birdiePoints + eaglePoints + acePoints;

  // Apply course difficulty multiplier
  const finalPoints = rawPoints * courseMultiplier;

  // Add point breakdown to player data
  player.points = {
    rankPoints: baseRankPoints,
    birdiePoints: birdiePoints,
    eaglePoints: eaglePoints,
    acePoints: acePoints,
    rawTotal: rawPoints,
    courseMultiplier: courseMultiplier,
    finalTotal: finalPoints
  };
});

// Return complete round data with points
return [{
  json: {
    roundInfo: {
      courseName: roundData.courseName,
      layoutName: roundData.layoutName,
      date: roundData.date,
      time: roundData.time,
      location: roundData.location,
      temperature: roundData.temperature,
      wind: roundData.wind,
      courseMultiplier: Number(courseMultiplier) // Force it to be a number
    },
    holes: roundData.holes,
    players: roundData.players
  }
}];

// Get the response from Anthropic
const anthropicResponse = $input.all()[0].json;

// Extract the text content
const textContent = anthropicResponse.content[0].text;

// Remove markdown code blocks if present
let cleanJson = textContent.trim();
if (cleanJson.startsWith('```json')) {
  cleanJson = cleanJson.replace(/```json\n?/g, '').replace(/```\n?/g, '');
}

// Parse the JSON
const parsedData = JSON.parse(cleanJson);

// ===== VALIDATION: Check for incomplete data =====
if (!parsedData.holes || parsedData.holes.length === 0) {
  throw new Error('Scorecard extraction failed: No hole data found. Please ensure the image is a valid UDisc scorecard.');
}

if (!parsedData.players || parsedData.players.length === 0) {
  throw new Error('Scorecard extraction failed: No player data found. Please ensure the image is a valid UDisc scorecard.');
}

// Check if all players have hole-by-hole data
const incompletePlayer = parsedData.players.find(p => !p.holeByHole || p.holeByHole.length === 0);
if (incompletePlayer) {
  throw new Error(`Scorecard extraction failed: Player "${incompletePlayer.name}" has no hole-by-hole scores. Please ensure the scorecard image is clear and complete.`);
}

// Verify and recalculate player stats based on actual par values
const holes = parsedData.holes;

parsedData.players.forEach(player => {
  let birdies = 0, eagles = 0, aces = 0, pars = 0, bogeys = 0, doubleBogeys = 0;

  player.holeByHole.forEach((score, index) => {
    // Safety check: Make sure the hole exists
    if (!holes[index] || holes[index].par === undefined) {
      console.log(`Warning: Hole ${index + 1} data missing. Skipping this hole for player ${player.name}`);
      return; // Skip this hole
    }

    const par = holes[index].par;
    const diff = score - par;

    if (score === 1) {
      aces++;
    } else if (diff <= -2) {
      eagles++;
    } else if (diff === -1) {
      birdies++;
    } else if (diff === 0) {
      pars++;
    } else if (diff === 1) {
      bogeys++;
    } else if (diff >= 2) {
      doubleBogeys++;
    }
  });

  // Update the player's stats with verified counts
  player.birdies = birdies;
  player.eagles = eagles;
  player.aces = aces;
  player.pars = pars;
  player.bogeys = bogeys;
  player.doubleBogeys = doubleBogeys;
});

// Helper function to find the first birdie hole
function getFirstBirdieHole(player, holes) {
  for (let i = 0; i < player.holeByHole.length; i++) {
    // Safety check: Make sure hole data exists
    if (!holes[i] || holes[i].par === undefined) {
      continue; // Skip this hole
    }

    const score = player.holeByHole[i];
    const par = holes[i].par;
    if (score === par - 1 && score > 1) { // Birdie (not ace)
      return i + 1; // Return hole number (1-indexed)
    }
  }
  return 999; // No birdie found
}

// Apply tie-breaker logic and sort players
const players = parsedData.players;

players.sort((a, b) => {
  // Primary: Total score (lower is better, e.g., -6 beats -4)
  if (a.totalScore !== b.totalScore) {
    return a.totalScore - b.totalScore;
  }

  // Tie-breaker 1: More birdies wins
  if (a.birdies !== b.birdies) {
    return b.birdies - a.birdies;
  }

  // Tie-breaker 2: More pars wins
  if (a.pars !== b.pars) {
    return b.pars - a.pars;
  }

  // Tie-breaker 3: Earlier first birdie wins
  const aFirstBirdie = getFirstBirdieHole(a, holes);
  const bFirstBirdie = getFirstBirdieHole(b, holes);

  if (aFirstBirdie !== bFirstBirdie) {
    return aFirstBirdie - bFirstBirdie;
  }

  // Tie-breaker 4: If all equal, they're tied
  return 0;
});

// Assign ranks with proper tie handling
let currentRank = 1;
for (let i = 0; i < players.length; i++) {
  if (i > 0) {
    const prev = players[i - 1];
    const curr = players[i];

    // Check if tied with previous player
    const isTied = 
      prev.totalScore === curr.totalScore &&
      prev.birdies === curr.birdies &&
      prev.pars === curr.pars &&
      getFirstBirdieHole(prev, holes) === getFirstBirdieHole(curr, holes);

    if (!isTied) {
      currentRank = i + 1;
    }
  }

  players[i].rank = currentRank;
}

// Return the complete structured data
return [{
  json: {
    courseName: parsedData.courseName,
    layoutName: parsedData.layoutName,
    date: parsedData.date,
    time: parsedData.time,
    location: parsedData.location,
    temperature: parsedData.temperature,
    wind: parsedData.wind,
    holes: parsedData.holes,
    players: players
  }
}];

const data = $input.all()[0].json;

// Parse the players JSON string into an actual array
const players = JSON.parse(data.players);

// Return the parsed data
return [{
  json: {
    round_id: data.round_id,
    players: players
  }
}];

const question = $input.all()[0].json.body.question;

// Simple keyword detection to determine query type
let queryType = 'general';
let searchTerm = '';

const lowerQuestion = question.toLowerCase();

// Detect query type
if (lowerQuestion.includes('leaderboard') || lowerQuestion.includes('standings') || lowerQuestion.includes('winning')) {
  queryType = 'leaderboard';
} else if (lowerQuestion.includes('stats for') || lowerQuestion.includes('stats on') || lowerQuestion.includes('player')) {
  queryType = 'player_stats';

  // Extract player name - look for text after "for" or "on"
  let nameMatch = question.match(/(?:for|on)\s+(.+?)$/i);
  if (nameMatch && nameMatch[1]) {
    searchTerm = nameMatch[1].trim();
  }
} else if (lowerQuestion.includes('course') || lowerQuestion.includes('at ')) {
  queryType = 'course_stats';
} else if (lowerQuestion.includes('round') || lowerQuestion.includes('game') || lowerQuestion.includes('recent')) {
  queryType = 'recent_rounds';
}

console.log('Question:', question);
console.log('Query Type:', queryType);
console.log('Search Term:', searchTerm);

return [{
  json: {
    question: question,
    queryType: queryType,
    searchTerm: searchTerm
  }
}];

// Get the original question
const question = $('Determine Query Type').item.json.question;

// Get all the data from Supabase queries
const allData = $input.all();

// Combine all query results
let combinedData = [];
allData.forEach(item => {
  if (item.json && Array.isArray(item.json)) {
    combinedData = combinedData.concat(item.json);
  } else if (item.json) {
    combinedData.push(item.json);
  }
});

// Prepare context for Claude
return [{
  json: {
    question: question,
    data: combinedData,
    dataCount: combinedData.length
  }
}];

// Get Claude's response
